var value;
$(document).ready(function() {
    var t = new Array;
    $(".stati18n-language-selector").each(function () {
        t.push(this.getAttribute("value"))
    }); {
        p = t[0]
    };
    $(".stati18n").addClass(p);
    $(".stati18n-language-selector").click(function (e) {
        $(".stati18n-language-selector").click(false);
        $("." + p).addClass("fadeout");
        $(".to-fade").addClass("fadeout");
        value = this.getAttribute("value");
        setTimeout(function() {
            $("." + p).removeClass("fadeout");
            $(".to-fade").removeClass("fadeout");
            $("." + p).removeClass(p).addClass(value);
            p = value;
            $(".stati18n-language-selector").click(true);
        }, 1000);
    });
});